var http=require('http');
var options={
	hostname:'127.0.0.1',
	port:1338,
	path:'/',
	method:'POST'
};
var req=http.request(options,function(res){
	res.on('data',function(data){
		console.log('客户端接到数据：'+data);//接收服务器端write()方法发送过来的数据
		
 	});
 	res.on('end',function(){//当上面的data事件不出发时，下面的res.trailers={}
	console.log('Trailer头信息：%j',res.trailers);
})
});
req.write('你好');
req.end('再见');

   